/* @ngInject */
module.exports = function (BaseModel) {
  return BaseModel.extend({
    modelType: 'FixedCost',

    props: {
      roundId: 'number',
      name: 'string',
      description: 'string',
      amount: 'decimal',
    },

    initialize: function () {
      Object.defineProperties(this, {
        amountDollars: {
          get: function () {
            return Number(this.amount.toFixed(2));
          },
          set: function (amountDollars) {
            this.amount = amountDollars || 0;
          },
        },
      });
    },
  });
};
